//12. Increment & Decrement:- Declare x = 10.- Apply prefix and postfix.

package Variables;

public class PrePostfix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int a1=++a;
		System.out.println("Prefix of a:"+a1);//prefix ++a
		int a2=a++;
		System.out.println("Postfix of a:"+a2);//postfix a++

	}

}
