//13. Salary Increment:- Store salary.- Add 10% increment.
package Variables;

public class SalaryIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double salary=80000;
		System.out.println("Salary :"+salary);
		double increment=salary*0.10; //10%increment
		double incSalary=increment+salary;
		System.out.println("After Incremented Salary :"+incSalary);

	}

}
