//11. Temperature Converter:- Convert Fahrenheit → Celsius.

package Variables;

public class Formula {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double Fahrenheit=98.2;
		double Celsius= (Fahrenheit-32)*5/9;        //formula=C=(F−32)×5/9​
		System.out.println(Celsius);

	}

}
